sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {},

    isDatePast: function(startDate) {
        // Create GlideDateTime objects for the current date and the passed in date
        var rightNow = new GlideDateTime();
        var testDate = new GlideDateTime(startDate);

        // If the testDate is before rightNow, return true else return false
        if (testDate.before(rightNow)) {
            return true;
        } else {
            return false;
        }
    },
    isDateToday: function(startDate) {
        // Challenge: Do not allow same - day requests
        // Get the date portion of rightNow and whenNeeded
        var today = new GlideDateTime().getLocalDate();
        var whenDay = new GlideDateTime(startDate).getLocalDate();

        // Compare today and startDate to see if they are the same day
        if (today.compareTo(whenDay) === 0) {
            return true;
        } else {
            return false;
        }
    },
    type: 'NeedItUtils'
};
